#!/bin/bash
#
# IDs in Tsinghua
#
# To create a_passwd[i] and a_user[i], cmd in the following is used:
#      $decoder -e -l $((365 * 24 * 3600 * 5)) <string>
#

decoder=${1?"*** what decoder ?"}

typeset -a a_passwd
a_passwd[0]=" "

typeset -a a_user
a_user[0]=" 011001101100100101001011011010100011101110101011001111100101000001100110110010000111101000011111011001101100100001101100110110100110011011001000010111110100111001100110110010000101001001000010011001101100100001000100111101000110011011001000001101110111000101100110110010000010101000101110"
a_user[1]=" "

cat << EOF
-------------------------------------------------------------------------------
URL  : https://id.tsinghua.edu.cn/
UID  : $($decoder -d ${a_user[0]})
PWD  : $($decoder -d ${a_passwd[0]})
MAIL : $($decoder -d ${a_user[0]})@mails.tsinghua.edu.cn
-------------------------------------------------------------------------------
URL  : http://www.newsmth.net/
UID  : $($decoder -d ${a_user[1]})
PWD  : $($decoder -d ${a_passwd[0]})
CELL : 135.*1
-------------------------------------------------------------------------------
EOF
